mapper.update:
UPDATE gga_coupon
SET used = '1'
WHERE id = #{id} and couponid=#{couponid}

UPDATE gga_coupon
SET used = '0'
WHERE id = #{id} and couponid=#{couponid}

mapper.select:
select couponid, couponname, startdate, enddate, discount, id, used
from gga_coupon
where id=#{id} and used=#{used} and ccategory=#{ccategory}
